import React from 'react';
import "./bootstrap/bootstrap.min.css";


export default class Users extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }

  componentDidMount() {
    fetch("http://localhost:9112/api/items")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, items } = this.state;
    console.log(items);
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div class="container-fluid">
        <table class="table table-bordered">
          <thead class="bg-info text-dark">
            <tr>
              <th>Name</th>
              <th>checkin</th>
              <th>checkout</th>
              <th>total working hours</th>
            </tr>
          </thead>
          {items.map(item => (
            <tr>
            <td>{item.id}</td>
            <td>
              {item.name}
            </td>
            <td>
            <button class="btn btn-info btn-rounded"><i class="fa fa-edit"></i>Edit</button>
            <button class="btn btn-danger btn-rounded"><i class="fa fa-edit"></i>Delete</button>
            </td>
            </tr>
          ))}
        </table>
        </div>
      );
    }
  }
}
